import * as BooksAPI from "../BooksAPI";
import BookList from "../bookList/bookList";



const BookShelf = () => {

   // [{wantToRead: 'Want to Read'}, {currentlyReading: 'Currently Reading'}, {read: 'read'}]

    const [bookShelf, setBookShelf] = useState(['currentlyReading', 'wantToRead', 'read']);


const bookShelfView = bookShelf.map((shelf, index) => {

    return <BookList shelf={shelf}> </BookList>

  })
    return (

            <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
            </div>
         {bookShelfView}
        </div>              
    )

    }

    export default BookShelf;
