import * as BooksAPI from "../BooksAPI";
import Book from "../book/book";



const BookList = (props) => {

    const [bookList, updateBooks] = useState({
        books: []});

useEffect(() => {
    console.log('here we are!');
    BooksAPI.getAll()
    .then((res) => {
        console.log(res);
        updateBooks({books: res});


    })
    .catch((err) => {
      console.log(err);
    })

}, [])
const bookShelfs = bookList.books.filter((book=>book.shelf === props.shelf));
const bookListView = bookShelfs.map((book, index) => {

    return <Book title={book.title}  imageUrl={book.imageLinks.thumbnail} author={book.authors[0]}></Book>

  })
    return (
        <ol>
         <div> {props.shelf} </div>
         <li> {bookListView } </li>
        </ol>              
    )

    }

    export default BookList;
