import ChangeShelfOptions from "../changeShelfOptions/changeShelfOptions";
import * as BooksAPI from "../../BooksAPI";

const Book = (props) => {



  const changeBookStatus = (status, book) => {
    BooksAPI.update(book,status)
      .then((res) => {
        console.log(res);
        BooksAPI.getAll()
      .then((res) => {
        console.log(res);
        useState.updateBooks({ books: res });
      })
      .catch((err) => {
        console.log(err);
      });
      })
      .catch((err) => {
        console.log(err);
      });
  }

return (
    <div>
        <div className="book">
                        <div className="book-top">
                          <div
                            className="book-cover"
                            style={{
                              width: 128,
                              height: 193,
                              backgroundImage:
                                `url(${props.imageUrl})`,
                            }}
                          ></div>
                          <ChangeShelfOptions book={props.book} shelf={props.shelf} changeBookStatus= {changeBookStatus}></ChangeShelfOptions>
                        </div>
                        <div className="book-title"> {props.title} </div>
                        <div className="book-authors">{props.author}</div>
                      </div>


    </div>
)
}

export default Book;
