import React from "react";
import { useNavigate } from 'react-router-dom';
import * as BooksAPI from "../../BooksAPI";
import { useState} from "react";
import Book from "../book/book";
const Search = () => {
    const navigateTo = useNavigate('');
    const searchResult = [];
    //const [searchResult, setSearchResult] = useState({books: []});
   const searchForBook = (event) => {
    setTimeout(() => {
        console.log(event.target.value);
        BooksAPI.search(event.target.value, 100)
      .then((res) => {
        console.log(res);
        searchResult = res;
        //setSearchResult({books: res})
        console.log(searchResult.books);

      })
      .catch((err) => {
        console.log(err);
      });
    }, 1000);   

    }
    const searchView = () => {
        console.log('result', searchResult.books);
        if(searchResult.length) {



             searchResult.map((book)=> {
                return( <Book
                title={book.title}
                imageUrl={book.imageLinks.thumbnail}
                author={book.authors[0]}
                // shelf={props.shelf}
                book = {book}
                // updateBookStauts = {updateStatus}
              ></Book>)

            })

        }
        else {
           return ( <div> No Data Match</div>)
        }


    }
  return (
    <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => navigateTo('/')}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"  onChange={searchForBook} 
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <div className="search-books-results">
            {searchView}
          </div>
        </div>
  );
};

export default Search;
